import React from "react";

const InitialPage: React.FC = () => {
  const generateRandomString = (length: number): string => {
    let text = "";
    const possible =
      "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789";
    for (let i = 0; i < length; i++) {
      text += possible.charAt(Math.floor(Math.random() * possible.length));
    }
    return text;
  };
  // logging the redirect uri into the console to make sure the url is correct
  console.log(process.env.REACT_APP_SPOTIFY_REDIRECT_URI);
  // env variables for auth
  const client_id: string = process.env.REACT_APP_SPOTIFY_CLIENT_ID as string;
  const redirect_uri: string = process.env
    .REACT_APP_SPOTIFY_REDIRECT_URI as string;

  const auth_endpoint = process.env.AUTH_ENDPOINT;
  const state: string = generateRandomString(16);
  const scope: string = "user-read-private user-read-email";
  const spotifyAuthUrl =
    `${auth_endpoint}?` +
    `response_type=code` +
    `&client_id=${client_id}` +
    `&scope=${encodeURIComponent(scope)}` +
    `&redirect_uri=${encodeURIComponent(redirect_uri)}` +
    `&state=${encodeURIComponent(state)}`;

  return (
    <div className="main-container">
      <h1>Display your playlists!</h1>
      <h2>
        In order to see your playlists, you need to click the button below.
      </h2>
      <a href={spotifyAuthUrl}>
        <button className="spotify-btn">Log in with Spotify</button>
      </a>
    </div>
  );
};

export default InitialPage;
